mapper.insert:
insert into summoner_spell(summoner_spell_name,summoner_spell_desc,summoner_spell_id,summoner_spell_key
) values(
#{summonerSpellName},#{summonerSpellDesc},#{summonerSpellId},#{summonerSpellKey}
)
ON CONFLICT (summoner_spell_key) DO UPDATE SET
summoner_spell_name = EXCLUDED.summoner_spell_name,
summoner_spell_desc = EXCLUDED.summoner_spell_desc,
summoner_spell_id = EXCLUDED.summoner_spell_id
